import './App.css';

import React, { useState, useEffect, useRef } from 'react';

const App = () => {
  const [messages, setMessages] = useState([]);
  const chatContainerRef = useRef(null);



  // 处理音频按钮点击
  const handleAudioCall = () => {
    setMessages(prev => [...prev, "音频通话已发起..."]);
    // 在这里添加实际的音频通话功能
  };

  // 处理视频按钮点击
  const handleVideoCall = () => {
    setMessages(prev => [...prev, "视频通话已发起..."]);
    // 在这里添加实际的视频通话功能
  };

  return (
    <div className="flex flex-col h-screen">
      {/* 聊天容器 */}
      <div 
        ref={chatContainerRef}
        className="flex-1 overflow-y-auto p-5 bg-white flex flex-col"
      >
        {messages.map((message, index) => (
          <div 
            key={index} 
            className="max-w-4/5 p-3 mb-3 rounded-2xl bg-gray-200 self-start shadow-sm"
          >
            {message}
          </div>
        ))}
      </div>

      {/* 操作栏 */}
      <div className="h-16 flex justify-around items-center bg-gray-800 p-3">
        <button 
          onClick={handleAudioCall}
          className="w-5/12 h-10 rounded-full bg-blue-500 hover:bg-blue-600 text-white font-bold flex items-center justify-center gap-2 transition-colors"
        >
          <AudioIcon />
          音频
        </button>
        <button 
          onClick={handleVideoCall}
          className="w-5/12 h-10 rounded-full bg-red-500 hover:bg-red-600 text-white font-bold flex items-center justify-center gap-2 transition-colors"
        >
          <VideoIcon />
          视频
        </button>
      </div>
    </div>
  );
};

// 音频图标组件
const AudioIcon = () => (
  <svg 
    xmlns="http://www.w3.org/2000/svg" 
    width="18" 
    height="18" 
    viewBox="0 0 24 24" 
    fill="none" 
    stroke="currentColor" 
    strokeWidth="2" 
    strokeLinecap="round" 
    strokeLinejoin="round"
  >
    <path d="M12 2a3 3 0 0 0-3 3v7a3 3 0 0 0 6 0V5a3 3 0 0 0-3-3Z"></path>
    <path d="M19 10v2a7 7 0 0 1-14 0v-2"></path>
    <line x1="12" y1="19" x2="12" y2="22"></line>
  </svg>
);

// 视频图标组件
const VideoIcon = () => (
  <svg 
    xmlns="http://www.w3.org/2000/svg" 
    width="18" 
    height="18" 
    viewBox="0 0 24 24" 
    fill="none" 
    stroke="currentColor" 
    strokeWidth="2" 
    strokeLinecap="round" 
    strokeLinejoin="round"
  >
    <path d="m22 8-6 4 6 4V8Z"></path>
    <rect x="2" y="6" width="14" height="12" rx="2" ry="2"></rect>
  </svg>
);

export default App;
