import React, { useState } from 'react';

const SimpleInterface = () => {
  const [displayText, setDisplayText] = useState('在这里显示文本内容');
  const [inputText, setInputText] = useState('');



  const handleSubmit = () => {
    if (inputText.trim()) {
      setDisplayText(inputText);
      setInputText('');
    }
  };

  const handleClear = () => {
    setDisplayText('');
    setInputText('');
  };

  const handleUpperCase = () => {
    setDisplayText(displayText.toUpperCase());
  };

  const handleLowerCase = () => {
    setDisplayText(displayText.toLowerCase());
  };

  // 内联样式
  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      minHeight: '100vh',
      backgroundColor: '#f3f4f6',
      padding: '16px'
    },
    card: {
      width: '100%',
      maxWidth: '500px',
      backgroundColor: 'white',
      borderRadius: '8px',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
      padding: '24px',
      margin: '16px'
    },
    title: {
      fontSize: '20px',
      fontWeight: 'bold',
      textAlign: 'center',
      marginBottom: '16px'
    },
    displayContainer: {
      marginBottom: '24px'
    },
    displayArea: {
      width: '100%',
      height: '192px',
      padding: '16px',
      backgroundColor: '#f9fafb',
      borderRadius: '8px',
      border: '1px solid #d1d5db',
      overflowY: 'auto'
    },
    text: {
      whiteSpace: 'pre-line'
    },
    inputContainer: {
      width: '100%'
    },
    formGroup: {
      display: 'flex',
      flexDirection: 'column',
      gap: '12px'
    },
    input: {
      width: '100%',
      padding: '8px',
      border: '1px solid #d1d5db',
      borderRadius: '8px',
      outline: 'none'
    },
    buttonGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(2, 1fr)',
      gap: '8px'
    },
    button: {
      padding: '8px 16px',
      color: 'white',
      borderRadius: '8px',
      cursor: 'pointer',
      border: 'none',
      outline: 'none'
    },
    submitButton: {
      backgroundColor: '#3b82f6'
    },
    clearButton: {
      backgroundColor: '#6b7280'
    },
    upperCaseButton: {
      backgroundColor: '#10b981'
    },
    lowerCaseButton: {
      backgroundColor: '#8b5cf6'
    }
  };

  // 在较大屏幕上更改按钮网格布局
  if (window.innerWidth >= 640) {
    styles.buttonGrid.gridTemplateColumns = 'repeat(4, 1fr)';
  }

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2 style={styles.title}>conversation with misty</h2>

        {/* 中央文本显示框 */}
        <div style={styles.displayContainer}>
          <div style={styles.displayArea}>
            <p style={styles.text}>{displayText}</p>
          </div>
        </div>

        {/* 底部输入区域 */}
        <div style={styles.inputContainer}>
          <div style={styles.formGroup}>
            <input
              type="text"
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              style={styles.input}
              placeholder="请输入文本..."
            />

            <div style={styles.buttonGrid}>
              <button
                onClick={handleSubmit}
                style={{...styles.button, ...styles.submitButton}}
              >
                send
              </button>
              <button
                onClick={handleClear}
                style={{...styles.button, ...styles.clearButton}}
              >
                clear
              </button>
              <button
                onClick={handleUpperCase}
                style={{...styles.button, ...styles.upperCaseButton}}
              >
                audio
              </button>
              <button
                onClick={handleLowerCase}
                style={{...styles.button, ...styles.lowerCaseButton}}
              >
                vedio
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SimpleInterface;
