import { useState } from 'react';

const SimpleInterface = () => {
  const [displayText, setDisplayText] = useState('在这里显示文本内容');
  const [inputText, setInputText] = useState('');

  const handleSubmit = () => {
    if (inputText.trim()) {
      setDisplayText(inputText);
      setInputText('');
    }
  };

  const handleClear = () => {
    setDisplayText('');
    setInputText('');
  };

  const handleUpperCase = () => {
    setDisplayText(displayText.toUpperCase());
  };

  const handleLowerCase = () => {
    setDisplayText(displayText.toLowerCase());
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
      <div className="w-full max-w-lg bg-white rounded-lg shadow-lg p-6 m-4">
        <h2 className="text-xl font-bold text-center mb-4">文本处理工具</h2>
        {/* 中央文本显示框 */}
        <div className="mb-6">
          <div className="w-full h-48 p-4 bg-gray-50 rounded-lg border border-gray-300 overflow-auto">
            <p className="whitespace-pre-line">{displayText}</p>
          </div>
        </div>

        {/* 底部输入区域 */}
        <div className="w-full">
          <div className="flex flex-col gap-3">
            <input
              type="text"
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="请输入文本..."
            />

            <div className="grid grid-cols-2 sm:grid-cols-4 gap-2">
              <button
                onClick={handleSubmit}
                className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                提交
              </button>
              <button
                onClick={handleClear}
                className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-500"
              >
                清空
              </button>
              <button
                onClick={handleUpperCase}
                className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-500"
              >
                大写
              </button>
              <button
                onClick={handleLowerCase}
                className="px-4 py-2 bg-purple-500 text-white rounded-lg hover:bg-purple-600 focus:outline-none focus:ring-2 focus:ring-purple-500"
              >
                小写
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SimpleInterface;
